import{_ as a,r as n,o,c as s,d as t,e,b as r,f as c}from"./app-22cd133c.js";const d={},l=t("h2",{id:"简介",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#简介","aria-hidden":"true"},"#"),e(" 简介")],-1),g=t("figure",null,[t("img",{src:"https://jihulab.com/byzh123/images/-/raw/images/pictures/2022/08/9_15_43_3_96147ae47d5ce12d3b8b974ede9463b9_f7246b600c338744a9591cd7530fd9f9d62aa0f8.png",alt:"简介",tabindex:"0",loading:"lazy"}),t("figcaption",null,"简介")],-1),p=t("p",null,"Git 是一个开源的分布式版本控制系统，用于敏捷高效地处理任何或小或大的项目。",-1),_=t("p",null,"Git 是 Linus Torvalds 为了帮助管理 Linux 内核开发而开发的一个开放源码的版本控制软件。",-1),h=t("p",null,"Git 与常用的版本控制工具 CVS, Subversion 等不同，它采用了分布式版本库的方式，不必服务器端软件支持。",-1),u={href:"http://git-scm.com/",target:"_blank",rel:"noopener noreferrer"},f=c('<h2 id="git-与-svn-区别" tabindex="-1"><a class="header-anchor" href="#git-与-svn-区别" aria-hidden="true">#</a> Git 与 SVN 区别</h2><p>Git 不仅仅是个版本控制系统，它也是个内容管理系统(CMS)，工作管理系统等。</p><p>如果你是一个具有使用 SVN 背景的人，你需要做一定的思想转换，来适应 Git 提供的一些概念和特征。</p><p>Git 与 SVN 区别点：</p><p>1、Git 是分布式的，SVN 不是：这是 Git 和其它非分布式的版本控制系统，例如 SVN，CVS 等，最核心的区别。</p><p>2、Git 把内容按元数据方式存储，而 SVN 是按文件：所有的资源控制系统都是把文件的元信息隐藏在一个类似 .svn、.cvs 等的文件夹里。</p><p>3、Git 分支和 SVN 的分支不同：分支在 SVN 中一点都不特别，其实它就是版本库中的另外一个目录。</p><p>4、Git 没有一个全局的版本号，而 SVN 有：目前为止这是跟 SVN 相比 Git 缺少的最大的一个特征。</p><p>5、Git 的内容完整性要优于 SVN：Git 的内容存储使用的是 SHA-1 哈希算法。这能确保代码内容的完整性，确保在遇到磁盘故障和网络问题时降低对版本库的破坏。</p><figure><img src="https://jihulab.com/byzh123/images/-/raw/images/pictures/2022/08/9_15_42_52_df37243a21c1b006fd27baf6e786bd69_0D32F290-80B0-4EA4-9836-CA58E22569B3.jpg" alt="Git VS SVN" tabindex="0" loading="lazy"><figcaption>Git VS SVN</figcaption></figure><h2 id="git工作机制" tabindex="-1"><a class="header-anchor" href="#git工作机制" aria-hidden="true">#</a> Git工作机制</h2><figure><img src="https://jihulab.com/byzh123/images/-/raw/images/pictures/2024/02/13_21_33_11_202402132133319.png" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><ul><li><strong>工作区</strong>写代码，通过<code>git add</code>命令添加至<strong>暂存区</strong></li><li><strong>暂存区</strong>临时存储代码，通过<code>git commit</code>提交至<strong>本地库</strong></li><li><strong>本地库</strong>记录历史记录，通过<code>git push</code>推送至<strong>远程库</strong></li></ul>',13);function m(b,S){const i=n("ExternalLinkIcon");return o(),s("div",null,[l,g,p,_,h,t("blockquote",null,[t("p",null,[e("官网地址："),t("a",u,[e("http://git-scm.com/"),r(i)])])]),f])}const G=a(d,[["render",m],["__file","10.html.vue"]]);export{G as default};
